ul.li:
take apart the “surely safe” items from the list. These are negative items
appearing on the left side, or positive items appearing on the right side;
they are safe because they are not going to have any collision.

pre.code:
#!/usr/bin/env raku
use v6;
sub MAIN (*@args) { put '(', number-collision(@args).join(', '), ')' }

sub number-collision (@list is copy) {
   my (@pre, @post); # safe items on the left and on the right

   loop {
      @pre.push:     @list.shift while @list && @list[0] < 0;
      @post.unshift: @list.pop   while @list && @list[*-1] > 0;
      last if @list == 0;

      my @mid;
      INDEX:
      for ^@list -> $i {
         my $item = @list[$i];
         if $item > 0 { # try to move right
            @mid.push: $item if $item + @list[$i + 1] > 0;
         }
         elsif $item < 0 { # try to move left
            @mid.push: $item if $item + @list[$i - 1] < 0;
         }
         else { # try to stay put
            my $safe-left  = ($i == 0) || (@list[$i - 1] <= 0);
            my $safe-right = ($i == @list.end) || (@list[$i + 1] >= 0);
            @mid.push: 0 if $safe-left && $safe-right;
         }
      }

      # stop simulation if nothing changed in this pass
      last if @list.elems == @mid.elems;

      # go to next iteration with surviving items in the middle
      @list = @mid;
   }

   return (@pre, @list, @post).flat.Array;
}

#!/usr/bin/perl

use strict;
use v5.10;

my %hash;
$hash{$_}++ for @ARGV;

my $max;
for my $int (keys %hash) {
    my $total = (($int - 1) * $hash{$int - 1}) + ($int * $hash{$int}) + (($int + 1) * $hash{$int + 1});
    $max = $total if $total > $max or !defined($max);
}

say $max if defined $max;

#!/usr/bin/perl

use strict;
use v5.10;

my @list = @ARGV;
for (my $index = 0; $index < $#list; $index++) {
    my $collision = $list[$index] + $list[$index + 1];
    if ($collision > $list[$index]) {next}
    elsif ($collision > 0) {
        splice @list, $index + 1, 1;
        $index--;
    } elsif ($collision == 0) {
        splice @list, $index, 2;
        $index -= 2;
    } elsif (0 > $collision > $list[$index + 1]) {
        splice @list, $index, 1;
        for (my $index2 = $index - 1; $index2 >= 0 and $list[$index2] > 0; $index2--) {
            if (-$list[$index] > $list[$index2]) {
                splice @list, $index2, 1;
                $index--;
                $index2--;                
            } elsif (-$list[$index] == $list[$index2]) {
                splice @list, $index2, 2;
                $index -= 2;
            } else {
                splice @list, $index, 1;
                $index--;
            }
        }
    }
}
say $_ for @list;

#!/usr/bin/env raku
use v6;
sub MAIN (*@args) { put kill-and-win(@args) }

sub kill-and-win-basic (@args) { return @args.sum }

sub kill-and-win (@args) {
   my $best-score = 0;
   my $score = 0;
   my $previous = 0;
   my $n-streak = 0;
   sub close-streak {
      return if $n-streak < 1;
      $score = $previous if $n-streak == 1; # "singleton"
      $best-score = $score if $score > $best-score;
      $score = 0;
      $n-streak = 0;
   }

   for @args.sort({$^a <=> $^b}) -> $item {
      close-streak() if $item > $previous + 1;
      $n-streak++    if $item > $previous;
      $score += $item;
      $previous = $item;
   }
   close-streak();

   return $best-score;
}
